mapper.select:
SELECT 
FROM users
WHERE phone = #{phone} AND password = #{password}

mapper.insert:
INSERT INTO users (
    phone, password, name, gender, id_card, community_name, 
    building_number, unit_number, room_number, area_size, is_verified, created_at
) VALUES (
    #{phone}, #{password}, #{name}, #{gender}, #{idCard}, #{communityName},
    #{buildingNumber}, #{unitNumber}, #{roomNumber}, #{areaSize}, #{isVerified}, #{createdAt}
)

mapper.select:
SELECT id 
FROM users 
WHERE phone = #{phone} AND id != #{excludeId}

SELECT COUNT(*) 
FROM user_votes 
WHERE user_id = #{userId} AND question_id = #{questionId}

mapper.insert:
INSERT INTO user_votes (
    user_id, vote_activity_id, question_id, option_id, 
    vote_method, vote_time, area_size
) VALUES (
    #{userId}, #{activityId}, #{questionId}, #{optionId}, 
    #{voteMethod}, #{voteTime}, #{areaSize}
)
